mapper.sql:
where 1=1   and deleted =  0


	    and id =  #{id}


	 	and name like CONCAT('%', #{name}, '%')


	    and url =  #{url}



	    and parentId =  #{parentId}



	    and parentId is null



	    and createTime =  #{createTime}


	    and rank =  #{rank}


	    and actions =  #{actions}

mapper.select:
	select 

	,(SELECT COUNT(*) FROM sys_menu c  WHERE c.parentId = m.id and deleted = 0) AS subCount
	from sys_menu m ORDER BY m.rank asc


      ${pager.orderCondition}


       ${pager.mysqlQueryCondition}

	select id,name,url,parentId,actions from sys_menu where deleted= 0 and parentId is null

     and  id != #{menuId}

	 order by rank asc

SELECT id,NAME,url,parentId,actions FROM sys_menu m WHERE deleted= 0 AND parentId IS NOT NULL
AND EXISTS (
	 SELECT * FROM sys_role_rel r WHERE r.objId = m.id AND relType = 0
	 AND r.roleId = #{roleId}
)  order by m.rank asc

SELECT DISTINCT id,NAME,url,parentId,actions FROM sys_menu m 
WHERE deleted= 0 AND parentId IS NULL
AND EXISTS (
	 SELECT * FROM sys_role_rel r 
	 WHERE r.objId = m.id AND r.relType = 0
	 AND EXISTS ( 
		SELECT 1 FROM sys_role_rel u WHERE u.roleId = r.roleId  AND u.relType = 1 AND u.objId = #{userId}
	 )
)  order by m.rank asc

SELECT DISTINCT id,NAME,url,parentId,actions FROM sys_menu m 
WHERE deleted= 0 AND parentId IS NOT NULL
AND EXISTS (
	 SELECT * FROM sys_role_rel r 
	 WHERE r.objId = m.id AND r.relType = 0
	 AND EXISTS ( 
		SELECT 1 FROM sys_role_rel u WHERE u.roleId = r.roleId  AND u.relType = 1 AND u.objId = #{userId}
	 )
)  order by m.rank asc
